-- ----------------------------
-- Function structure for `sequence_currval`
-- ----------------------------
DROP FUNCTION IF EXISTS `sequence_currval`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `sequence_currval`(seq_name VARCHAR(50)) RETURNS int(11)
    DETERMINISTIC
BEGIN
     DECLARE value INTEGER; 
     SET value = 0; 
     SELECT current_value INTO value 
          FROM t_sequence
          WHERE name = seq_name; 
     RETURN value; 
END
;;
DELIMITER ;

-- ----------------------------
-- Function structure for `sequence_nextval`
-- ----------------------------
DROP FUNCTION IF EXISTS `sequence_nextval`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `sequence_nextval`(seq_name VARCHAR(50)) RETURNS int(11)
    DETERMINISTIC
BEGIN
     UPDATE t_sequence
          SET current_value = current_value + increment 
          WHERE name = seq_name; 
     RETURN sequence_currval(seq_name); 
END
;;
DELIMITER ;

-- ----------------------------
-- Function structure for `sequence_setval`
-- ----------------------------
DROP FUNCTION IF EXISTS `sequence_setval`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `sequence_setval`(seq_name VARCHAR(50), value INTEGER) RETURNS int(11)
    DETERMINISTIC
BEGIN
     UPDATE t_sequence
          SET current_value = value 
          WHERE name = seq_name; 
     RETURN sequence_currval(seq_name); 
END
;;
DELIMITER ;
